"use strict";(self.webpackChunk_niivue_docs=self.webpackChunk_niivue_docs||[]).push([[849],{6164:i=>{i.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"link","label":"Getting started","href":"/docs/","docId":"index","unlisted":false},{"type":"link","label":"Loading images","href":"/docs/Loading images","docId":"Loading images","unlisted":false},{"type":"link","label":"Using Colormaps","href":"/docs/colormaps","docId":"colormaps","unlisted":false},{"type":"link","label":"Layouts and Slice Types","href":"/docs/layouts","docId":"layouts","unlisted":false},{"type":"link","label":"Synchronizing Multiple Views","href":"/docs/syncing","docId":"syncing","unlisted":false},{"type":"link","label":"Statistical Thresholding","href":"/docs/thresholding","docId":"thresholding","unlisted":false},{"type":"link","label":"Drawing and Segmentation","href":"/docs/drawing","docId":"drawing","unlisted":false}]},"docs":{"colormaps":{"id":"colormaps","title":"Using Colormaps","description":"Colormaps are essential for visualizing data, as they map the values within your images to specific colors displayed on the screen. Niivue provides a range of built-in colormaps and allows you to define your own custom maps.","sidebar":"tutorialSidebar"},"drawing":{"id":"drawing","title":"Drawing and Segmentation","description":"Niivue provides powerful tools for manual and semi-automatic segmentation of volumetric medical images. These tools allow users to create, edit, and save regions of interest (ROIs) as masks.","sidebar":"tutorialSidebar"},"index":{"id":"index","title":"Getting started","description":"NiiVue is an all-in-one package for parsing, loading, and viewing medical imaging data in the browser.","sidebar":"tutorialSidebar"},"layouts":{"id":"layouts","title":"Layouts and Slice Types","description":"NiiVue supports many scene and slice configurations. Common layouts are provided out of the box, but custom layouts can be created too.","sidebar":"tutorialSidebar"},"Loading images":{"id":"Loading images","title":"Loading images","description":"Generic loading","sidebar":"tutorialSidebar"},"syncing":{"id":"syncing","title":"Synchronizing Multiple Views","description":"When working with medical imaging data, it\'s often useful to have multiple synchronized views of the same or different volumes. Niivue provides robust synchronization capabilities that allow you to coordinate navigation, interaction, and visualization across multiple viewers.","sidebar":"tutorialSidebar"},"thresholding":{"id":"thresholding","title":"Statistical Thresholding","description":"Statistical thresholding is a crucial technique in neuroimaging and other medical imaging disciplines, allowing researchers to visualize only statistically significant regions in their data. Niivue provides powerful and flexible thresholding capabilities that enable users to display both positive and negative statistical values, customize thresholds, and adjust visualization options around these thresholds.","sidebar":"tutorialSidebar"}}}}')}}]);